import React, { useState } from "react";
import styled from "styled-components";
import CharacterHeader from "../../Componets/CharacterSheet/CharacterHeader";
import BaseStats from "../../Componets/CharacterSheet/BaseStats";
import characterRollDisplay from "../../Logic/characterRollDisplay";

const primaryColor = "#662d91";
const secondaryColor = "#d0bee4";

const DisplayGrid = styled.div`
  margin: 0 auto;
  display: grid;
`;

const BaseStatsContainer = styled.div`
  width: 100%;
  height: 110px;
  display: flex;
`;

const TempRollDisplay = styled.div`
  width: 250px;
  height: 100px;
  border: 3px solid ${primaryColor};
  background-color: #fff;
  position: absolute;
  right: 3vw;
  bottom: 12vh;
`;

const CharacterSheet = ({ character }) => {
  const [lastRoll, setLastRoll] = useState("");

  return (
    <DisplayGrid>
      <CharacterHeader
        hp={character.hp}
        charClass={character.class}
        race={character.race}
      />
      <BaseStatsContainer>
        <BaseStats stats={character.stats} />
      </BaseStatsContainer>
      <TempRollDisplay>{characterRollDisplay[0]}</TempRollDisplay>
    </DisplayGrid>
  );
};

export default CharacterSheet;
