import React from "react";
import styled from "styled-components";
import D20 from "../../Images/Dice/D20";

const StyledD20 = styled(D20)`
  margin-top: 55px;
  height: 180px;
`;

const DiceNumber = styled.p`
  font-family: "Dosis";
  font-size: 2.8em;
  text-align: center;
  position: relative;
  left: 50%;
  transform: translate(-50%, -295%);
`;

const TempButton = styled.button`
  border-radius: 360px;
  outline: none;
  padding: 25px 30px;
  font-size: 1.25em;
  margin: 0 auto;
  text-decoration: none;
`;

class DiceRoller extends React.Component {
  state = { number: 20 };

  rollDice = (dice) => {
    this.setState({ number: dice });
    console.log(this.number);
  };

  render() {
    return (
      <div>
        <StyledD20 />
        <DiceNumber>{this.state.number}</DiceNumber>
        <TempButton onClick={() => rollDice(20)}>20</TempButton>
      </div>
    );
  }
}

export default DiceRoller;
