import React, { useState } from "react";
import styled from "styled-components";
import theme from "../../Logic/theme";

const { primary, seconday, tri, quad, primaryFont, background } = theme;

const Container = styled.div`
  grid-area: actions;
  min-width: 32vw;
  min-height: 55vh;
  width: 98%;
  height: 150%;
  margin-right: 6em;
  height: 40vh;
  background-color: ${background};
  border: 3px solid ${primary};
  border-radius: 5px;
  display: flex;
  flex-wrap: wrap;

  @media (max-width: 600px) {
    margin: 0 auto;
    min-height: 10em;
    max-height: 20em;
    width: 95%;
    flex-wrap: wrap-reverse;
  }
`;

const Panel = styled.div`
  width: 95%;
  height: 85%;
  margin: 0.25em auto 0;
  background-color: ${primaryFont};
  border-radius: 5px;
`;

const Menu = styled.div`
  margin: 0.25em auto 0;
  display: flex;
  align-items: center;
`;

const MenuItem = styled.div`
  border: 2px solid ${primary};
  border-radius: 5px;
  width: 3em;
  margin: 0 0.25em;
  padding: 0.25em;
  color: ${primaryFont};
  :hover {
    background-color: ${tri};
    cursor: pointer;
  }
`;

const ActionsContainer = () => {
  const [active, setActive] = useState("Action");

  const changeScreen = (type) => {
    setActive(type);
    console.log(active);
  };
  return (
    <Container>
      <Menu>
        <MenuItem onClick={() => changeScreen("Action")}>Action</MenuItem>
        <MenuItem>Attack</MenuItem>
        <MenuItem>Spells</MenuItem>
        <MenuItem>Bonus</MenuItem>
        <MenuItem>Other</MenuItem>
      </Menu>
      <Panel></Panel>
    </Container>
  );
};

export default ActionsContainer;
