import React from "react";
import styled from "styled-components";

const primaryColor = "#662d91";
const secondaryColor = "#d0bee4";

const Container = styled.div`
  display: flex;
  width: 100%;
  height: 45px;
  background-color: ${secondaryColor};
  border-bottom: 3px solid ${primaryColor};
`;

const StyledP = styled.p`
  margin: 15px 0 0 15px;
`;

const CharacterHeader = ({ race, hp, charClass }) => {
  let levelTotal = 0;
  const totalLevel = () => {
    for (let i = 0; i < charClass.length; i + 1) {
      levelTotal = levelTotal + charClass[i].level;
    }
  };
  totalLevel();
  return (
    <Container>
      <StyledP>{`${race} Level ${levelTotal}`}</StyledP>
    </Container>
  );
};

export default CharacterHeader;
