import React from "react";
import styled from "styled-components";
import theme from "../Logic/theme";

const { primary, secondary, tri, quad, background, primaryFont } = theme;

const StyledButton = styled.div`
  background-color: #662d91;
  color: #fff;
  border-radius: 25px;
  min-width: 80px;
  max-width: 250px;
  border: 1px solid #3e1b6b;
  min-height: 10px;
  max-height: 35px;
  text-align: center;
  &:hover {
    cursor: pointer;
    background-color: #3e1b6b;
  }
`;

const StyledText = styled.p`
  margin: auto;
  margin: 10px 0;
  font-size: 0.9em;
`;

const Link = styled.a`
  text-decoration: none;
`;

const Button = ({ text, href }) => {
  return (
    <Link href={href}>
      <StyledButton>
        <StyledText>{text}</StyledText>
      </StyledButton>
    </Link>
  );
};

export default Button;
