import React from "react";
import react from "react";
import styled from "styled-components";
import CharacterRollDisplay from "./ChracterRollDIsplay";

const primaryColor = "#662d91";
const secondaryColor = "#252525";

const Container = styled.div`
  width: 100%;
  margin: 0 auto;
  font-family: "Dosis";
  font-size: 1.5em;
  font-weight: bold;
`;

const SkillContainer = styled.div`
  margin-top: 1em;
  width: 100%;
  display: flex;
  border-bottom: 1px solid ${primaryColor};
`;

const SkillBox = styled.div`
  height: 25px;
  margin: 0 0.5em 10px;
`;

const IsPro = styled.div`
  width: 10px;
  height: 10px;
  margin: 0.4em 10px 0;
  border: 1px solid ${primaryColor};
  border-radius: 25px;
  background-color: ${secondaryColor};
`;

const NotPro = styled.div`
  width: 10px;
  height: 10px;
  border: 1px solid ${primaryColor};
  border-radius: 25px;
  background-color: #fff;
`;

const Skills = ({ skills }) => {
  const RenderedSkills = skills.map((skills) => {
    return (
      <React.Fragment>
        <SkillContainer>
          <SkillBox>
            <IsPro />
          </SkillBox>
          <SkillBox>{skills.mod}</SkillBox>
          <SkillBox>{skills.name}</SkillBox>
          <SkillBox>{skills.bonus}</SkillBox>
        </SkillContainer>
      </React.Fragment>
    );
  });

  return (
    <Container>
      <SkillContainer>
        <SkillBox>
          <IsPro />
        </SkillBox>
        <SkillBox>{skills[0].mod}</SkillBox>
        <SkillBox>{skills[0].name}</SkillBox>
        <SkillBox>{skills[0].bonus}</SkillBox>
      </SkillContainer>
    </Container>
  );
};

export default Skills;
