import React from "react";
import styled from "styled-components";
import Header from "./header";
import PlayArea from "./MainPlayArea";
import Footer from "./footer";
import SideButton from "../Buttons/SideMenuButton";
import D20 from "../Images/Dice/D20";

const PlayContainer = styled.div`
  text-align: center;
`;

const RollButton = styled(SideButton)`
  top: 75vh;
`;
const HistoryButton = styled(SideButton)`
  top: 65vh;

  @media (max-width: 850px) {
    top: 68vh;
  }

  @media (max-width: 600px) {
    top: 66vh;
  }
`;

const D20Button = styled(D20)`
  width: 40px;
  position: fixed;
  left: 20px;
  top: 80vh;
`;

class MainContainer extends React.Component {
  render() {
    return (
      <PlayContainer>
        <Header />
        <PlayArea />
        <Footer />
        <RollButton />
        <D20Button />
        <HistoryButton />
      </PlayContainer>
    );
  }
}
export default MainContainer;
