import React from "react";
import styled from "styled-components";
import D20 from "../../Images/Dice/D20Icon";
import D12 from "../../Images/Dice/D12Icon";
import D8 from "../../Images/Dice/D8Icon";
import D6 from "../../Images/Dice/D6Icon";
import D4 from "../../Images/Dice/D4Icon";

const RollButton = styled.div`
  width: 80px;
  height: 80px;
  position: relative;
  border-radius: 50%;
  background-color: #662d91;
  border: 3px solid #3e1b6b;
  margin: 0 auto;

  :hover {
    background-color: #3e1b6b;
    cursor: pointer;
  }
`;

const StyledD20 = styled(D20)`
  margin-top: 8px;
  width: 55px;
`;

const StyledD12 = styled(D12)`
  margin-top: 11px;
  width: 55px;
`;

const StyledD8 = styled(D8)`
  margin-top: 12px;
  width: 50px;
`;

const StyledD6 = styled(D6)`
  margin-top: 18px;
  width: 45px;
`;

const StyledD4 = styled(D4)`
  margin-top: 12px;
  width: 48px;
`;

const ButtonShell = (props) => {
  if (props.dice === 20) {
    return (
      <React.Fragment>
        <RollButton onClick={props.onClick}>
          <StyledD20 button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  }
  if (props.dice === 12) {
    return (
      <React.Fragment>
        <RollButton onClick={props.onClick}>
          <StyledD12 button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  }
  if (props.dice === 8) {
    return (
      <React.Fragment>
        <RollButton onClick={props.onClick}>
          <StyledD8 button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  }
  if (props.dice === 6) {
    return (
      <React.Fragment>
        <RollButton onClick={props.onClick}>
          <StyledD6 button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  }
  if (props.dice === 4) {
    return (
      <React.Fragment>
        <RollButton onClick={props.onClick}>
          <StyledD4 button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  } else {
    console.log("Roll Button Generator Error");
  }
};

export default ButtonShell;
