import React from "react";
import styled from "styled-components";

const primaryColor = "#662d91";
const secondaryColor = "#d0bee4";
const darkgray = "#252525";

const Container = styled.div`
  width: 90px;
  height: 90px;
  margin: 7px 5px 0 5px;
  background-color: #fff;
  border: solid 3px ${primaryColor};
  border-radius: 10px;

  @media (max-width: 600px) {
    width: 57px;
    height: 80px;
    margin: 10px 2px 0px 3px;
  }
`;

const P = styled.p`
  margin-top: 5px;
  color: ${darkgray};
  @media (max-width: 600px) {
    display: none;
  }
`;

const Pmobile = styled.p`
  display: none;
  @media (max-width: 600px) {
    display: block;
  }
`;

const BaseStats = ({ stats }) => {
  const renderedStats = stats.map((stats) => {
    return (
      <Container key={stats.name}>
        <P>{stats.name}</P>
        <Pmobile>{stats.name.slice(0, 3)}</Pmobile>
      </Container>
    );
  });
  return <React.Fragment>{renderedStats}</React.Fragment>;
};

export default BaseStats;
