import React from "react";
import styled, { keyframes } from "styled-components";
import theme from "../../Logic/theme";
import D20 from "../../Images/Dice/D20Icon";
import D12 from "../../Images/Dice/D12Icon";

const { primary, background } = theme;

const rollAnimation = keyframes`
 0%
  {
    transform: rotate(0deg);
  }
  25%
  {
    transform: rotate(45deg);
  }
  50%
  {
    transform: rotate(-140deg)
  }
  75%
  {
    transform: rotate(120deg);
  }
  100%
  {
    transform: rotate(0deg);
  }
`;

const TempRollDisplay = styled.div`
  width: 300px;
  min-height: 125px;
  border: 3px solid ${primary};
  border-radius: 5px;
  background-color: #fff;
  position: fixed;
  right: 3vw;
  bottom: 3vh;
  z-index: 8;

  @media (max-width: 600px) {
    bottom: 8vh;
  }
`;

const StyledP = styled.p`
  font-family: "Dosis";
  margin-top: 5px;
  font-size: 1.5em;
  padding: 5px;
`;

const StyledP2 = styled.p`
  font-family: "Dosis";
  font-size: 1.5em;
  margin-top: -1.1em;
`;

const StyledD20 = styled(D20)`
  width: 48px;
  margin-top: -35px;
  animation-name: ${rollAnimation};
  animation-iteration-count: 1;
  animation-duration: 0.4s;

  @media (max-width: 600px) {
    margin-top: -19px;
  }
`;

const X = styled.p`
  font-family: "Dosis";
  margin-top: 0px;
  font-size: 1.5em;
  margin-left: 90%;
  font-weight: bold;
  color: ${primary};
  :hover {
    cursor: pointer;
    color: ${background};
  }

  @media (max-width: 600px) {
    font-size: 1.8em;
    margin-bottom: 0px;
  }
`;

const CharacterRollDisplay = ({ check, check2, num, onClick }) => {
  if (!check && !check2) {
    return <div />;
  }
  return (
    <TempRollDisplay key="Checks Display">
      <X key="Check Display Exit" onClick={onClick()}>
        X
      </X>
      <StyledD20 key={num} />
      <StyledP key={check}>{check}</StyledP>
      {check2 ? <StyledP2 key={check2}>{check2}</StyledP2> : null}
    </TempRollDisplay>
  );
};

export default CharacterRollDisplay;
