import React, { useState } from "react";
import styled from "styled-components";
import theme from "../../../Logic/theme";
import CharacterRollDisplay from "../ChracterRollDIsplay";

const { primary, secondary, tri, quad, background, primaryColor } = theme;

const Container = styled.div`
  width: 95%;
  margin: 0 auto;
  overflow: scroll;
`;

const Row = styled.div`
  width: 90%;
  height: 4.2em;
  margin: 0 auto;
  display: flex;
  border-bottom: 1px solid ${primary};
`;

const NameBox = styled.div`
  display: block;
  text-align: left;
  margin-left: 1em;
  min-width: 9em;

  @media (max-width: 600px) {
    margin-left: 2.2em;
  }
`;

const Title = styled.p`
  font-family: "Dosis";
  color: ${primary};
  font-size: 1.3em;
  margin-bottom: 1;
`;

const Type = styled.div`
  font-size: 0.7em;
  margin-top: -1.8em;
  margin-left: 0.5em;
`;

const RangeBox = styled.div`
  margin-top: 1.2em;
  width: 2em;
  @media (max-width: 600px) {
    margin-left: 0.1em;
  }
`;

const Range = styled.p`
  font-size: 0.8em;
  font-weight: bold;
  text-align: left;
`;

const RollBox = styled.div`
  border: 2px solid ${primary};
  border-radius: 5px;
  min-width: 2em;
  height: 2em;
  margin-top: 1.3em;
  margin-left: 1.5em;
  :hover {
    cursor: pointer;
    background-color: ${secondary};
  }
`;

const BoxP = styled.p`
  font-family: "Dosis";
  margin-top: 0.35em;
  color: ${background};
`;

const Attack = ({ attacks }) => {
  const [lastRoll, setLastRoll] = useState("");
  const [numOfRoll, setNumofRoll] = useState(0);

  const renderedItems = attacks.map((attacks) => {
    return (
      <Row key={`${attacks.name} row`}>
        <NameBox key={`${attacks.name} name box`}>
          <Title key={attacks.name}>{attacks.name}</Title>
          <Type key={`${attacks.type}`}>{attacks.type}</Type>
        </NameBox>
        <RangeBox key={`${attacks.name} range box`}>
          <Range key={`${attacks.name} range`}>{attacks.range}ft.</Range>
        </RangeBox>
        <RollBox key={`${attacks.name} ${attacks.hit} box`}>
          <BoxP key={`${attacks.name} ${attacks.hit}`}>+{attacks.hit}</BoxP>
        </RollBox>
      </Row>
    );
  });
  return <Container>{renderedItems}</Container>;
};

export default Attack;
