import React from "react";
import styled from "styled-components";
import D20 from "../../Images/Dice/D20Icon";
import D12 from "../../Images/Dice/D12Icon";

const RollButton = styled.div`
  width: 80px;
  height: 80px;
  position: relative;
  border-radius: 50%;
  background-color: #662d91;
  border: 3px solid #3e1b6b;
  margin: 0 auto;

  :hover {
    background-color: #3e1b6b;
    cursor: pointer;
  }
`;

const StyledD20 = styled(D20)`
  margin-top: 8px;
  width: 55px;
`;

const StyledD12 = styled(D12)`
  margin-top: 12px;
  width: 55px;
`;

const ButtonShell = (props) => {
  if (props.dice === 20) {
    return (
      <React.Fragment>
        <RollButton onClick={props.onClick}>
          <StyledD20 button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  }
  if (props.dice === 12) {
    return (
      <React.Fragment>
        <RollButton onClick={props.onClick}>
          <StyledD12 button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  } else {
    return <div>I am not a button</div>;
  }
};

export default ButtonShell;
