import React from "react";
import styled from "styled-components";
import Footer from "../Footer";
import Logo from "../../Images/Logo";
import InputField from "../../Componets/InputField";

const Container = styled.div`
  background-color: #252525;
  width: 99vw;
  height: calc(100vh - 75px);
  text-align: center;
  margin: 0 auto;

  @media (max-width: 600px) {
    width: 100vw;
  }
`;

const InLine = styled.div`
  display: flex;
  width: 100%;
  background-color: green;
`;

const LoginBox = styled.div`
  width: 90vw;
  max-width: 600px;
  min-height: 375px;
  margin: 0 auto;
  text-align: center;
  background-color: #fff;
  border: solid 4px #662d91;
  border-radius: 5px;
  position: absolute;
  top: 50vh;
  left: 50vw;
  transform: translate(-50%, -60%);

  @media (max-width: 600px) {
    margin-left: 7px;
  }
`;

const StyledLogo = styled(Logo)`
  width: 275px;
  margin: -15px auto 0;
`;

const H1 = styled.h1`
  font-size: 2.5em;
  color: #662d91;
`;

const H2 = styled.p`
  font-size: 1.5em;
  font-family: "Dosis";
  margin-top: -2px;
  color: #662d91;
`;

const Form = styled.form`
  width: 20vw;
  display: block;
  margin: 0 auto;
`;

const InputBox = styled.div`
  margin-top: -20px;
`;

const Link = styled.a`
  color: #662d91;
  font-size: 0.8em;
  text-decoration: underline;

  &:hover {
    cursor: pointer;
  }
`;

const Login = () => {
  return (
    <div>
      <Container>
        <LoginBox>
          <H1>Welcome To</H1>
          <StyledLogo />
          <H2>
            Alpha <span style={{ fontSize: 10 }}>V 0.2</span>
          </H2>
          <Form>
            <InputBox>
              <InputField placeholder="User Name" type="email" />
              <InputField placeholder="Password" type="password" />
            </InputBox>
            <InLine>
              <Link>Request an invite</Link>
              <button>Login</button>
            </InLine>
            <br />
            <br />
          </Form>
          <hr />
          <br />
          <form>
            <label>I Just Want to Roll Some Dice </label>
            <button>Dice Roller</button>
          </form>
        </LoginBox>
      </Container>
      <Footer />
    </div>
  );
};

export default Login;
