import React from "react";
import styled from "styled-components";
import D20 from "../Images/Dice/D20";
import D12 from "../Images/Dice/D12";
import D8 from "../Images/Dice/D8";
import D6 from "../Images/Dice/D6";
import D4 from "../Images/Dice/D4";

const StyledD20 = styled(D20)`
  margin-top: 55px;
  height: 180px;
`;

const StyledD12 = styled(D12)`
  margin-top: 55px;
  height: 170px;
`;

const StyledD8 = styled(D8)`
  margin-top: 55px;
  height: 180px;
`;

const StyledD6 = styled(D6)`
  margin-top: 75px;
  height: 150px;
  box-sizing: border-box;
  padding: 10px;
`;
const StyledD4 = styled(D4)`
  margin-top: 55px;
  height: 160px;
  pad
`;

const DiceDisplay = ({ dice }) => {
  if (dice === 20) {
    return <StyledD20 />;
  }
  if (dice === 12) {
    return <StyledD12 />;
  }
  if (dice === 8) {
    return <StyledD8 />;
  }
  if (dice === 6) {
    return <StyledD6 />;
  }

  if (dice === 6) {
    return <StyledD6 />;
  }
  if (dice === 4) {
    return <StyledD4 />;
  }
};

export default DiceDisplay;
