import React from "react";
import styled from "styled-components";
import CharacterHeader from "../../Componets/CharacterSheet/CharacterHeader";
import BaseStats from "../../Componets/CharacterSheet/BaseStats";
import Skills from "../../Componets/CharacterSheet/Skills";

const primaryColor = "#662d91";
const secondaryColor = "#d0bee4";

const DisplayGrid = styled.div`
  width: 100%;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-template-rows: 65px 1fr 1fr 1fr 1fr 1fr;
  row-gap: 0.5em;
  grid-template-areas:
    " header header header header"
    "BaseStat Base Base Base"
    "Skill Skill Temp Temp";

  @media (max-width: 600px) {
    grid-template-areas:
      "header header header header"
      "BaseStat BaseStat BaseStat BaseStat"
      "Base Base Skill Skill";
  }
`;

const BaseStatsContainer = styled.div`
  width: 100%;
  display: flex;
  margin-top: -0.5em;
  grid-area: BaseStat;

  @media (max-width: 600px) {
    margin-top: -1em;
  }
`;

const TempBase = styled.div`
  background-color: darkgreen;
  grid-area: Base;
  width: 100%;
`;
const SkillsContainer = styled.div`
  grid: Skill;
  text-align: center;
  background-color: lightgoldenrodyellow;
`;

const CharacterSheet = ({ character }) => {
  return (
    <DisplayGrid>
      <CharacterHeader
        hp={character.hp}
        charClass={character.class}
        race={character.race}
      />
      <BaseStatsContainer>
        <BaseStats stats={character.stats} />
      </BaseStatsContainer>
      <SkillsContainer />
      <TempBase>I am temp base</TempBase>
      <SkillsContainer>
        <Skills skills={character.skills} />
      </SkillsContainer>
    </DisplayGrid>
  );
};

export default CharacterSheet;
