import React from "react";
import styled from "styled-components";
import D20 from "../../Images/Dice/D20Icon";

const RollButton = styled.button`
  width: 80px;
  height: 80px;
  position: relative;
  border-radius: 50%;
  left: 15%;
  top: 20%;
  background-color: #662d91;
  text-decoration: none;

  :hover {
    background-color: #3e1b6b;
    cursor: pointer;
  }
`;

const StyledD20 = styled(D20)`
  margin-top: 8px;
  width: 55px;
`;

const ButtonShell = ({ dice }) => {
  rollDice = (dice) => {
    this.setState({ rolled: Math.floor(Math.random() * dice) + 1 });
  };

  if (dice === 20) {
    return (
      <React.Fragment>
        <RollButton>
          <StyledD20 button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  } else {
    return <div>I am not a button</div>;
  }
};

export default ButtonShell;
