import React from "react";
import styled from "styled-components";
import Footer from "../Footer";
import Logo from "../../Images/Logo";
import InputField from "../../Componets/InputField";
import Button from "../../Buttons/Button";
import Pathway from "../../Images/Pathway";

const main = "#662d91";

const Container = styled.div`
  background-color: #252525;
  width: 99vw;
  height: calc(100vh - 75px);
  text-align: center;
  margin: 0 auto;
  overflow: hidden;

  @media (max-width: 600px) {
    width: 100vw;
  }
`;

const InLine = styled.div`
  display: inline-flex;
  width: 100%;
  max-width: 400px;
  margin-top: 5px;
  justify-content: space-around;
`;

const LoginBox = styled.div`
  width: 90vw;
  max-width: 600px;
  min-height: 375px;
  margin: 0 auto;
  text-align: center;
  background-color: #fff;
  border: solid 4px ${main};
  border-radius: 5px;
  position: absolute;
  top: 50vh;
  left: 50vw;
  transform: translate(-50%, -60%);

  @media (max-width: 600px) {
    margin-left: 7px;
  }
`;

const StyledLogo = styled(Logo)`
  width: 275px;
  margin: -15px auto 0;
`;

const H1 = styled.h1`
  font-size: 2.5em;
  color: ${main};
`;

const H2 = styled.p`
  font-size: 1.5em;
  font-family: "Dosis";
  margin-top: -2px;
  color: ${main};
`;

const Form = styled.form`
  width: 20vw;
  display: block;
  margin: 0 auto 10px;

  @media (max-width: 1200px) {
    margin: 35px auto 20px;
    width: 30vw;
  }

  @media (max-width: 900px) {
    margin: 35px auto 20px;
    width: 40vw;
  }

  @media (max-width: 600px) {
    margin-top: 10px;
    width: 70vw;
  }
`;

const InputBox = styled.div`
  margin-top: -20px;
`;

const Link = styled.p`
  color: ${main};
  font-size: 0.8em;
  text-decoration: underline;

  &:hover {
    cursor: pointer;
    color: #3e1b6b;
  }
`;

const Divider = styled.hr`
  border: 1px solid ${main};
`;

const Login = () => {
  return (
    <div>
      <Container>
        <Pathway
          color={main}
          width={300}
          style={{ postion: "relative", bottom: 0 }}
        />
        <LoginBox>
          <H1>Welcome To</H1>
          <StyledLogo />
          <H2>
            Alpha <span style={{ fontSize: 10 }}>V 0.2</span>
          </H2>
          <Form>
            <InputBox>
              <InputField placeholder="User Name" type="email" />
              <InputField placeholder="Password" type="password" />
            </InputBox>
            <InLine>
              <Link
                onClick={() => {
                  window.location = "mailto:john.jurmu@gmail.com";
                }}
              >
                Request an invite
              </Link>
              <Button text="Login" />
            </InLine>
          </Form>
          <Divider />
          <InLine>
            <div style={{ marginTop: 5 }}>
              <H2>I just want to roll some dice </H2>
            </div>
            <Button text={"Roll Here"} />
          </InLine>
        </LoginBox>
      </Container>
      <Footer />
    </div>
  );
};

export default Login;
