import React from "react";
import styled from "styled-components";
import D20 from "../../Images/Dice/D20";
import D20Icon from "../../Images/Dice/D20Icon";

const StyledD20 = styled(D20)`
  margin-top: 8px;
  width: 55px;
`;

const ButtonShell = ({ dice }) => {
  if (dice === 20) {
    return (
      <React.Fragment>
        <RollButton>
          <StyledD20Icon button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  } else {
    return <div>I am not a button</div>;
  }
};

const StyledD20Icon = styled(D20Icon)`
  margin-top: 55px;
  height: 180px;
`;

const DiceNumber = styled.p`
  font-family: "Dosis";
  font-size: 2.8em;
  text-align: center;
  position: relative;
  left: 50%;
  transform: translate(-50%, -295%);
`;

const TempButton = styled.button`
  border-radius: 360px;
  outline: none;
  padding: 25px 30px;
  font-size: 1.25em;
  margin: 0 auto;
  text-decoration: none;

  :hover {
    cursor: pointer;
  }
`;

class DiceRoller extends React.Component {
  state = { rolled: "R", type: 20 };

  rollDice = (dice) => {
    this.setState({ rolled: Math.floor(Math.random() * dice) + 1 });
    console.log("I am clicked button");
  };

  render() {
    return (
      <div>
        <StyledD20 />
        <DiceNumber>{this.state.rolled}</DiceNumber>
        <ButtonShell dice={20} />
      </div>
    );
  }
}

export default DiceRoller;
