import React, { useState } from "react";
import styled from "styled-components";
import diceRollResults from "../Logic/diceRollResults";
import D4Icon from "../Images/Dice/D4Icon";

const TotalsContainer = styled.div`
  margin: 0 auto;
  width: 95%;
  display: flex;
  justify-content: space-between;
  background-color: lightgray;

  @media (max-width: 900px) {
    width: 85%;
  }
`;

const Divider = styled.hr`
  border: 1px #662d91 solid;
  width: 95%;

  @media (max-width: 900px) {
    width: 85%;
  }
`;

const Row = styled.div`
  width: 100%;
  display: flex;
`;

const DiceIconBox = styled.div`
  height: 59px;
  width: 59px;
  background-color: #662d91;
`;

const SumIconBox = styled.div`
  height: 59px;
  width: 59px;
  background-color: #662d91;
  position: absolute;
  left: 90.5%;
`;

const RollTotalsText = styled.p`
  margin-left: 15px;
  font-size: 1.15em;
`;

const RollSumText = styled.p`
  font-size: 1.15em;
  color: #fff;
`;

const TotalsDisplay = () => {
  const [D4] = useState(diceRollResults.D4);

  const D4arr = () => {
    let str = "";
    for (let i = 0; i < diceRollResults.D4.length; i++) {
      str = `${str} + ${D4[i]}`;
    }
    return str.slice(3);
  };
  return (
    <React.Fragment>
      <Divider />
      <p style={{ marginBottom: 5 }}>Totals</p>
      <TotalsContainer>
        <Row>
          <DiceIconBox>
            <D4Icon width={45} style={{ marginTop: 5 }} />
          </DiceIconBox>
          <RollTotalsText>{D4arr()}</RollTotalsText>
          <SumIconBox>= 10</SumIconBox>
        </Row>
      </TotalsContainer>
    </React.Fragment>
  );
};

export default TotalsDisplay;
