import React from "react";
import styled from "styled-components";

const primaryColor = "#662d91";
const secondaryColor = "#d0bee4";
const darkgray = "#252525";

const Container = styled.div`
  width: 90px;
  height: 90px;
  margin: -10px 0 0 0;
  background-color: #fff;
  border: solid 3px ${primaryColor};
  border-radius: 10px;
  :hover {
    cursor: pointer;
  }

  @media (max-width: 600px) {
    width: 57px;
    height: 80px;
  }
`;
const RearContainer = styled.div`
  width: 90px;
  height: 90px;
  margin: 25px 0px 0 8px;
  background-color: ${darkgray};
  border: solid 3px ${primaryColor};
  border-radius: 10px;
  :hover {
    cursor: pointer;
  }

  @media (max-width: 600px) {
    width: 57px;
    height: 80px;
    margin: 10px 2px 0px 3px;
  }
`;

const P = styled.p`
  margin-top: 10px;
  color: ${darkgray};
  @media (max-width: 600px) {
    display: none;
  }
`;

const Pmobile = styled.p`
  display: none;
  @media (max-width: 600px) {
    display: block;
    margin-top: 6px;
  }
`;

const ModBox = styled.div`
  width: 50%;
  height: 35px;
  margin: 0 auto;
  border: 3px solid ${primaryColor};
  margin-top: -8px;
  @media (max-width: 600px) {
    margin-top: -15px;
  }
`;

const Mod = styled.p`
  font-family: "Dosis";
  font-size: 2em;
  text-align: center;
  color: ${primaryColor};
  margin-top: -3px;

  @media (max-width: 600px) {
    font-size: 1.7em;
    margin-top: 0px;
  }
`;

const BaseStats = ({ stats }) => {
  const renderedStats = stats.map((stats) => {
    return (
      <React.Fragment>
        <RearContainer>
          <Container key={stats.name}>
            <P>{stats.name}</P>
            <Pmobile>{stats.name.slice(0, 3)}</Pmobile>
            <ModBox>
              <Mod>{stats.mod}</Mod>
            </ModBox>
          </Container>
        </RearContainer>
      </React.Fragment>
    );
  });
  return <React.Fragment>{renderedStats}</React.Fragment>;
};

export default BaseStats;
