import React, { useState } from "react";
import styled from "styled-components";

const primaryColor = "#662d91";
const secondaryColor = "#252525";

const Container = styled.div`
  display: flex;
  width: 100%;
  height: 63px;
  background-color: ${secondaryColor};
  border-bottom: 4px solid ${primaryColor};

  @media (max-width: 600px) {
    height: 45px;
  }
`;

const LevelsInfo = styled.div`
  display: block;
`;

const StyledP = styled.p`
  margin: 15px 0 0 20px;
  color: #fff;
`;

const SmallText = styled.p`
  margin: 5px 0 0 0px;
  font-size: 0.7em;
  color: #fff;
`;

const HPcontainer = styled.div`
  width: 130px;
  height: 40px;
  position: absolute;
  display: flex;
  right: 30px;
  margin-top: 8px;
`;

const HPinput = styled.input`
  width: 25px;
  height: 20px;
  margin-left: 10px;
  margin-top: 10px;
  background-color: ${secondaryColor};
  border: ${primaryColor} 2px solid;
  color: white;
  padding-left: 8px;
  padding-top: 3px;
`;

const CharacterHeader = ({ race, hp, charClass }) => {
  const [currentHP, setCurrentHP] = useState(hp.current);

  const levelTotal = () => {
    let total = 0;
    charClass.forEach((e) => {
      total += e.level;
    });
    return total;
  };

  const classBreakdown = () => {
    let str = "";
    charClass.forEach((e) => {
      str += ` / ${e.class} ${e.level}`;
    });
    return str.slice(3);
  };
  return (
    <Container>
      <LevelsInfo>
        <StyledP>{`${race} | Level ${levelTotal()}`}</StyledP>
        <SmallText>{`(${classBreakdown()})`}</SmallText>
      </LevelsInfo>
      <HPcontainer>
        <StyledP>HP: </StyledP>
        <HPinput
          value={currentHP}
          onChange={(e) => setCurrentHP(e.target.value)}
        ></HPinput>
        <StyledP>{` / ${hp.max}`}</StyledP>
      </HPcontainer>
    </Container>
  );
};

export default CharacterHeader;
