import React, { useState } from "react";
import styled from "styled-components";
import rollDisplay from "../../Logic/characterRollDisplay";

const primaryColor = "#662d91";
const secondaryColor = "#d0bee4";
const darkgray = "#252525";

const Container = styled.div`
  width: 90px;
  height: 90px;
  margin: -25px 0 0 -3px;
  background-color: #fff;
  border: solid 3px ${primaryColor};
  border-radius: 10px;
  :hover {
    cursor: pointer;
  }

  @media (max-width: 600px) {
    width: 57px;
    height: 80px;
  }
`;
const RearContainer = styled.div`
  width: 90px;
  height: 90px;
  margin: 40px 0px 0 8px;
  background-color: ${darkgray};
  border: solid 3px ${primaryColor};
  border-radius: 10px;
  :hover {
    cursor: pointer;
  }

  @media (max-width: 600px) {
    width: 57px;
    height: 80px;
    margin: 30px 2px 0px 3px;
  }
`;

const P = styled.p`
  margin-top: 10px;
  color: ${darkgray};
  @media (max-width: 600px) {
    display: none;
  }
`;

const Pmobile = styled.p`
  display: none;
  @media (max-width: 600px) {
    display: block;
    margin-top: 6px;
  }
`;

const BaseP = styled.p`
  color: #fff;
  font-size: 0.75em;
  margin-top: 3px;
`;

const ModBox = styled.div`
  width: 50%;
  height: 35px;
  margin: 0 auto;
  border: 3px solid ${primaryColor};
  margin-top: -8px;
  @media (max-width: 600px) {
    margin-top: -15px;
  }
`;

