import React from "react";
import styled from "styled-components";
import theme from "../../Logic/theme";

const { primary, secondary, tri, quad, background, primaryFont } = theme;

const Container = styled.div`
  grid-area: misc;
  width: 100%;
  margin-top: 0.5em;
  max-height: 10em;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-evenly;

  @media (max-width: 600px) {
    position: relative;
    left: 1em;
  }
`;

const Box = styled.div`
  width: 5em;
  height: 5em;
  margin: 0.5em;
  background-color: #fff;
  border: 3px solid ${primary};
  border-radius: 5px;
  :hover {
    cursor: pointer;
    background-color: ${secondary};
  }

  @media (max-width: 600px) {
    width: 4em;
    height: 4em;
  }
`;

const StyledP = styled.p`
  font-size: 1em;
  margin-top: 0.3em;
`;

const BotomP = styled.p`
  font-size: 1em;
  margin-top: -2.2em;
`;

const Stat = styled.p`
  font-family: "Dosis";
  color: ${primary};
  font-size: 2em;
  margin-top: -0.55em;

  @media (max-width: 600px) {
    font-size: 1.6em;
    margin-top: -0.8em;
  }
`;

const MiscStats = ({ misc }) => {
  return (
    <Container>
      <Box>
        <StyledP>Armor</StyledP>
        <Stat>{misc.ac}</Stat>
        <BotomP>Class</BotomP>
      </Box>
      <Box>
        <StyledP>Initiative</StyledP>
        <Stat>{`+${misc.initiative}`}</Stat>
      </Box>
      <Box>
        <StyledP>Walking</StyledP>
        <Stat>{`${misc.speed} ft.`}</Stat>
        <BotomP>Speed</BotomP>
      </Box>
      <Box>
        <StyledP>Inspired</StyledP>
        <Stat>{misc.inspiration.toString()}</Stat>
      </Box>
      <Box>
        <StyledP>Rest</StyledP>
        <Stat>ZZZ</Stat>
      </Box>
    </Container>
  );
};

export default MiscStats;
