import React from "react";
import styled from "styled-components";
import D20 from "../../Images/Dice/D20";
import D20Icon from "../../Images/Dice/D20Icon";

const RollButton = styled.button`
  width: 80px;
  height: 80px;
  position: relative;
  border-radius: 50%;
  left: 15%;
  top: 20%;
  background-color: #662d91;
  text-decoration: none;

  :hover {
    background-color: #3e1b6b;
    cursor: pointer;
  }
`;

const StyledD20 = styled(D20Icon)`
  margin-top: 8px;
  width: 55px;
`;

const ButtonShell = ({ dice }) => {
  if (dice === 20) {
    return (
      <React.Fragment>
        <RollButton>
          <StyledD20Icon button={"yes"} />
        </RollButton>
      </React.Fragment>
    );
  } else {
    return <div>I am not a button</div>;
  }
};

const StyledD20Icon = styled(D20)`
  margin-top: 55px;
  height: 180px;
`;

const DiceNumber = styled.p`
  font-family: "Dosis";
  font-size: 2.8em;
  text-align: center;
  position: relative;
  left: 50%;
  transform: translate(-50%, -295%);
`;

const TempButton = styled.button`
  border-radius: 360px;
  outline: none;
  padding: 25px 30px;
  font-size: 1.25em;
  margin: 0 auto;
  text-decoration: none;

  :hover {
    cursor: pointer;
  }
`;

class DiceRoller extends React.Component {
  state = { rolled: "R", type: 20 };

  rollDice = (dice) => {
    this.setState({ rolled: Math.floor(Math.random() * dice) + 1 });
    console.log("I am clicked button");
  };

  render() {
    return (
      <div>
        <StyledD20 />
        <DiceNumber>{this.state.rolled}</DiceNumber>
        <ButtonShell dice={20} />
        <TempButton onClick={() => this.rollDice(20)}>20</TempButton>
      </div>
    );
  }
}

export default DiceRoller;
