import React from "react";
import styled from "styled-components";
import diceRollResults from "../Logic/diceRollResults";
import D4Icon from "../Images/Dice/D4Icon";
import D6Icon from "../Images/Dice/D6Icon";
import D8Icon from "../Images/Dice/D8Icon";
import D10Icon from "../Images/Dice/D10Icon";
import D12Icon from "../Images/Dice/D12Icon";
import D20Icon from "../Images/Dice/D20Icon";

const TotalsContainer = styled.div`
  margin: 0 auto;
  width: 95%;
  display: flex;
  justify-content: space-between;
  background-color: lightgray;

  @media (max-width: 900px) {
    width: 85%;
  }
`;

const Divider = styled.hr`
  border: 1px #662d91 solid;
  width: 95%;

  @media (max-width: 900px) {
    width: 85%;
  }
`;

const Row = styled.div`
  width: 100%;
  display: flex;
`;

const DiceIconBox = styled.div`
  height: 59px;
  width: 59px;
  background-color: #662d91;
`;

const RollDispContainer = styled.div`
  width: calc(100% - 118px);
  overflow-x: scroll;
  max-height: 59px;
  text-align: left;
`;

const SumIconBox = styled.div`
  height: 59px;
  width: 59px;
  background-color: #662d91;
`;

const RollTotalsText = styled.p`
  margin-left: 15px;
  font-size: 1.15em;
`;

const RollSumText = styled.p`
  font-size: 1.15em;
  color: #fff;
`;

const TotalsDisplay = () => {
  const rollsInfo = [
    {
      type: "D20",
      rolls: diceRollResults.D20,
      El: <D20Icon width={45} style={{ marginTop: 5 }} />,
    },
    {
      type: "D12",
      rolls: diceRollResults.D12,
      el: <D12Icon width={45} style={{ marginTop: 5 }} />,
    },
    {
      type: "D10",
      rolls: diceRollResults.D10,
      el: <D20Icon />,
    },
    {
      type: "D8",
      rolls: diceRollResults.D8,
      el: <D8Icon />,
    },
    {
      type: "D6",
      rolls: diceRollResults.D6,
      el: <D6Icon />,
    },
    {
      type: "D4",
      rolls: diceRollResults.D4,
      el: <D4Icon />,
    },
  ];
  const rollTableRender = rollsInfo.map(({ type, rolls, el }) => {
    return <Row key={`Roll Result ${type}`}>{el}</Row>;
  });

  const D4arr = () => {
    let str = "";
    for (const e of diceRollResults.D4) {
      str = `${str} + ${e}`;
    }
    return str.slice(3);
  };

  const D4Total = () => {
    let total = 0;
    for (const e of diceRollResults.D4) {
      total += e;
    }
    return total;
  };
  return (
    <React.Fragment>
      <Divider />
      <p style={{ marginBottom: 5 }}>Totals</p>
      <TotalsContainer>
        {rollTableRender}
        <Row>
          <DiceIconBox>
            <D4Icon width={45} style={{ marginTop: 5 }} />
          </DiceIconBox>
          <RollDispContainer>
            <RollTotalsText>{D4arr()}</RollTotalsText>
          </RollDispContainer>
          <SumIconBox>
            <RollSumText>= {D4Total()}</RollSumText>
          </SumIconBox>
        </Row>
      </TotalsContainer>
    </React.Fragment>
  );
};

export default TotalsDisplay;
