import React from "react";
import styled from "styled-components";
import DiceRollResults from "../Logic/DiceRollResults";
import D4Icon from "../Images/Dice/D4Icon";

const Container = styled.div`
  width: 95%;
  margin: 0 auto;
  margin-top: 35px;
  background-color: #fff;
  border: 3px solid #662d91;
`;

const CountContainer = styled.div`
  margin: 0 auto;
  width: 50%;
  display: flex;
  justify-content: space-between;

  @media (max-width: 600px) {
    width: 45%;
  }
`;

const DiceDisplay = styled.div`
  display: flex;
`;

const StyledD4 = styled(D4Icon)`
  width: 40px;
`;

const RollInfoDisplay = () => {
  return (
    <Container>
      <p>Number of Rolls</p>
      <CountContainer>
        <DiceDisplay>
          <StyledD4 />
          <p>X {DiceRollResults.D4.length} </p>
        </DiceDisplay>
        <p>D6 X {DiceRollResults.D6.length} </p>
        <p>D8 X {DiceRollResults.D8.length} </p>
        <p>D12 X {DiceRollResults.D12.length} </p>
        <p>D20 X {DiceRollResults.D20.length} </p>
      </CountContainer>
    </Container>
  );
};

export default RollInfoDisplay;
