import React from "react";
import styled from "styled-components";
import theme from "../../../Logic/theme";

const { primary, secondary, tri, quad, background, primaryColor } = theme;

const Container = styled.div`
  width: 95%;
  margin: 0 auto;
  overflow: scroll;
`;

const Row = styled.div`
  width: 90%;
  height: 4.2em;
  margin: 0 auto;
  display: flex;
  border-bottom: 1px solid ${primary};
`;

const NameBox = styled.div`
  display: block;
  text-align: left;
  margin-left: 1em;
  min-width: 9em;

  @media (max-width: 600px) {
    margin-left: 2.2em;
  }
`;

const Title = styled.p`
  font-family: "Dosis";
  color: ${primary};
  font-size: 1.3em;
  margin-bottom: 1;
`;

const Type = styled.div`
  font-size: 0.7em;
  margin-top: -1.8em;
  margin-left: 0.5em;
`;

const Range = styled.p`
  font-size: 0.8em;
  font-weight: bold;
  margin-top: 2.5em;
`;

const RollBox = styled.div`
  border: 2px solid ${primary};
  border-radius: 5px;
  width: 2em;
  height: 2em;
  margin-top: 1em;
`;

const BoxP = styled.p`
  font-family: "Dosis";
`;

const Attack = ({ attacks }) => {
  const renderedItems = attacks.map((attacks) => {
    return (
      <Row key={`${attacks.name} row`}>
        <NameBox key={`${attacks.name} name box`}>
          <Title key={attacks.name}>{attacks.name}</Title>
          <Type key={`${attacks.type}`}>{attacks.type}</Type>
        </NameBox>
        <Range key={`${attacks.name} range`}>{attacks.range}ft.</Range>
        <RollBox key={`${attacks.name} ${attacks.hit} box`}>
          <BoxP>{attacks.hit}</BoxP>
        </RollBox>
      </Row>
    );
  });
  return <Container>{renderedItems}</Container>;
};

export default Attack;
