import React, { useState } from "react";
import styled from "styled-components";
import CharacterHeader from "../../Componets/CharacterSheet/CharacterHeader";
import BaseStats from "../../Componets/CharacterSheet/BaseStats";
import Skills from "../../Componets/CharacterSheet/Skills";

const primaryColor = "#662d91";
const secondaryColor = "#d0bee4";

const DisplayGrid = styled.div`
  width: 100%;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-template-rows: repeat(4, 1fr);

  grid-template-areas:
    " header header header"
    "BaseState Base Base";
`;

const BaseStatsContainer = styled.div`
  width: 100%;
  height: 110px;
  display: flex;
  grid-area: BaseState;
`;

const CharacterSheet = ({ character }) => {
  const [lastRoll, setLastRoll] = useState("");

  return (
    <DisplayGrid>
      <CharacterHeader
        hp={character.hp}
        charClass={character.class}
        race={character.race}
      />
      <BaseStatsContainer>
        <BaseStats stats={character.stats} />
      </BaseStatsContainer>
    </DisplayGrid>
  );
};

export default CharacterSheet;
