import react from "react";
import styled, { keyframes } from "styled-components";
import D20 from "../../Images/Dice/D20Icon";

const primaryColor = "#662d91";
const secondaryColor = "#d0bee4";
const darkgray = "#252525";

const rollAnimation = keyframes`
 0%
  {
    transform: rotate(0deg);
  }
  25%
  {
    transform: rotate(45deg);
  }
  50%
  {
    transform: rotate(-140deg)
  }
  75%
  {
    transform: rotate(120deg);
  }
  100%
  {
    transform: rotate(0deg);
  }
`;

const TempRollDisplay = styled.div`
  width: 250px;
  height: 100px;
  border: 3px solid ${primaryColor};
  background-color: #fff;
  position: absolute;
  right: 3vw;
  bottom: 12vh;
`;

const StyledP = styled.p`
  font-family: "Dosis";
  margin-top: 0px;
  font-size: 1.2em;
`;

const CharacterRollDisplay = ({ check }) => {
  return (
    <TempRollDisplay>
      <D20 width={50} />
      <StyledP>{check}</StyledP>
    </TempRollDisplay>
  );
};

export default CharacterRollDisplay;
