import React from "react";
import styled from "styled-components";
import theme from "../../Logic/theme";
import CharacterRollDisplay from "./ChracterRollDIsplay";

const { primary, secondary, tri, quad, background, primaryFont } = theme;

const Container = styled.div`
  width: 100%;
  margin: 0 auto;
  font-family: "Dosis";
  font-size: 1.2em;
  font-weight: bold;
`;

const SkillContainer = styled.div`
  margin-top: 1em;
  width: 100%;
  display: flex;
  border-bottom: 1px solid ${primary};
`;

const SkillBox = styled.div`
  height: 25px;
  margin: 0 0.5em 0.25em;
`;

const ModBox = styled.div`
  text-align: center;
  background-color: ${background};
`;

const ProBox = styled.div`
  width: 0.5em;
`;

const IsPro = styled.div`
  width: 10px;
  height: 10px;
  margin: 0.4em 10px 0;
  border: 1px solid ${quad};
  border-radius: 25px;
  background-color: ${secondary};
`;

const NotPro = styled.div`
  width: 10px;
  height: 10px;
  border: 1px solid ${primary};
  border-radius: 25px;
  background-color: none;
`;

const Skills = ({ skills }) => {
  const RenderedSkills = skills.map((skills) => {
    return (
      <React.Fragment>
        <SkillContainer>
          <SkillBox>
            <IsPro />
          </SkillBox>
          <SkillBox>{skills.mod}</SkillBox>
          <SkillBox>{skills.name}</SkillBox>
          <ModBox>{skills.bonus}</ModBox>
        </SkillContainer>
      </React.Fragment>
    );
  });

  return <Container>{RenderedSkills}</Container>;
};

export default Skills;
