import React from "react";
import styled from "styled-components";
import Router from "../Componets/Route";
import GridIMG from "../Images/grid.svg";
import CharacterSheet from "./Pages/CharacterSheet";
import DiceRoller from "./Pages/DiceRoller";

const GridBackground = styled.div`
  background-color: rgba(79, 79, 79, 0.5);
  min-height: 75vh;
  height: 100%;
  border: 3px solid #662d91;
`;

const Container = styled.div`
  width: 99vw;
  min-height: 75vh;
  margin-top: 10px;
  background-image: url(${GridIMG});

  @media (max-width: 600px) {
    width: 100vw;
  }
`;

const MainPlayContainer = ({ character, page }) => {
  const setPage = (page) => {
    if (page === "freeroll") {
      return <DiceRoller />;
    }
    if (page === "character") {
      return <CharacterSheet character={character} />;
    }
  };

  return (
    <Container>
      <GridBackground>{setPage(page)}</GridBackground>
    </Container>
  );
};

export default MainPlayContainer;
