import React, { useState } from "react";
import styled from "styled-components";
import Route from "./Componets/Route";
import Header from "./Container/Header";
import Footer from "./Container/Footer";
import RollButton from "./Buttons/RollMenuItem";
import HistoryButton from "./Buttons/HistoryMenuItem";
import CharacterButton from "./Buttons/CharacterMenuItem";
import PlayArea from "./Container/MainPlayArea";
import Login from "./Container/Pages/Login";
import Character from "./Logic/tavadar";
import GridIMG from "./Images/grid.svg";

const PlayContainer = styled.div`
  text-align: center;
`;

const GridBackground = styled.div`
  background-color: rgba(79, 79, 79, 0.5);
  min-height: 75vh;
  height: 100%;
  border: 3px solid #662d91;
`;

const Container = styled.div`
  width: 99vw;
  min-height: 75vh;
  margin-top: 10px;
  background-image: url(${GridIMG});

  @media (max-width: 600px) {
    width: 100vw;
  }
`;

const App = () => {
  const [character] = useState(Character);

  return (
    <div>
      <Route path="/">
        <Login />
      </Route>
      <Route path="/app">
        <Header name={character.name} />
        <Container></Container>
        <Footer />
        <RollButton />
        <HistoryButton />
        <CharacterButton />
        <PlayArea />
      </Route>
    </div>
  );
};

export default App;

