import react from "react";
import styled, { keyframes } from "styled-components";
import D20 from "../../Images/Dice/D20Icon";

const primaryColor = "#662d91";
const secondaryColor = "#d0bee4";
const darkgray = "#252525";

const rollAnimation = keyframes`
 0%
  {
    transform: rotate(0deg);
  }
  25%
  {
    transform: rotate(45deg);
  }
  50%
  {
    transform: rotate(-140deg)
  }
  75%
  {
    transform: rotate(120deg);
  }
  100%
  {
    transform: rotate(0deg);
  }
`;

const TempRollDisplay = styled.div`
  width: 300px;
  height: 125px;
  border: 3px solid ${primaryColor};
  border-radius: 5px;
  background-color: #fff;
  position: absolute;
  right: 2vw;
  bottom: 12vh;

  @media (max-width: 600px) {
    bottom: 5vh;
  }
`;

const StyledP = styled.p`
  font-family: "Dosis";
  margin-top: 5px;
  font-size: 1.5em;
`;

const StyledD20 = styled(D20)`
  width: 48px;
  margin-top: -35px;
  animation-name: ${rollAnimation};
  animation-iteration-count: 1;
  animation-duration: 0.4s;

  @media (max-width: 600px) {
    margin-top: -19px;
  }
`;

const X = styled.p`
  font-family: "Dosis";
  margin-top: 0px;
  font-size: 1.5em;
  margin-left: 90%;
  font-weight: bold;
  color: ${primaryColor};
  :hover {
    cursor: pointer;
    color: ${darkgray};
  }

  @media (max-width: 600px) {
    font-size: 1.8em;
    margin-bottom: 0px;
  }
`;

const CharacterRollDisplay = ({ check, num, onClick }) => {
  if (!check) {
    return <div />;
  }
  return (
    <TempRollDisplay key="Checks Display">
      <X key="Check Display Exit" onClick={onClick()}>
        X
      </X>
      <StyledD20 key={num} />
      <StyledP key={check}>{check}</StyledP>
    </TempRollDisplay>
  );
};

export default CharacterRollDisplay;
