import React, { useState } from "react";
import styled from "styled-components";
import DiceRollResults from "../Logic/DiceRollResults";
import D4Icon from "../Images/Dice/D4Icon";
import D6Icon from "../Images/Dice/D6Icon";
import D8Icon from "../Images/Dice/D8Icon";
import D12Icon from "../Images/Dice/D12Icon";
import D20Icon from "../Images/Dice/D20Icon";

const Container = styled.div`
  width: 95%;
  margin: 0 auto;
  margin-top: 35px;
  background-color: #fff;
  border: 3px solid #662d91;

  @media (max-width: 600px) {
    width: 85%;
    margin-right: 10px;
    float: right;
    margin-top: 05px;
  }
`;

const CountContainer = styled.div`
  margin: 0 auto;
  width: 50%;
  display: flex;
  justify-content: space-between;

  @media (max-width: 600px) {
    width: 80%;
    flex-flow: wrap;
  }
`;

const DiceDisplay = styled.div`
  display: flex;
  margin-bottom: 15px;
`;

const Count = styled.p`
  margin-left: 10px;
`;

const RollInfoDisplay = () => {
  const [result4, setResult4] = useState(DiceRollResults.D4);
  const [result6, setResult6] = useState(DiceRollResults.D6);
  const [result8, setResult8] = useState(DiceRollResults.D8);
  const [result12, setResult12] = useState(DiceRollResults.D12);
  const [result20, setResult20] = useState(DiceRollResults.D20);

  const rollDisplay = [
    {
      dice: <D4Icon width={50} />,
      rollNum: result4,
    },
    {
      dice: <D6Icon width={50} />,
      rollNum: result6,
    },
    {
      dice: <D8Icon width={50} />,
      rollNum: result8,
    },
    {
      dice: <D12Icon width={50} />,
      rollNum: result12,
    },
    {
      dice: <D20Icon width={50} />,
      rollNum: result20,
    },
  ];

  const rollCounterDisplay = rollDisplay.map(({ dice, rollNum }) => {
    return (
      <DiceDisplay>
        {dice}
        <Count>x {rollNum.length}</Count>
      </DiceDisplay>
    );
  });

  return (
    <Container>
      <p>Number of Rolls</p>
      <CountContainer>{rollCounterDisplay}</CountContainer>
    </Container>
  );
};

//<RoundButton onClick={props.onClick}>Clear</RoundButton>

export default RollInfoDisplay;
