import React from "react";
import styled from "styled-components";
import theme from "../../Logic/theme";
import CharacterRollDisplay from "./ChracterRollDIsplay";

const { primary, secondary, tri, quad, background, primaryFont } = theme;

const Container = styled.div`
  grid-area: misc;
  width: 100%;
  margin-top: 0.5em;
  max-height: 10em;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-evenly;

  @media (max-width: 600px) {
    position: relative;
    left: 1em;
  }
`;

const Box = styled.div`
  width: 5em;
  height: 5em;
  margin: 0.5em;
  background-color: ${background};
  border: 3px solid ${primary};
  border-radius: 5px;

  @media (max-width: 600px) {
    width: 6em;
    height: 4em;
    margin: 0.3em;
  }
`;

const InitBox = styled.div`
  width: 5em;
  height: 5em;
  margin: 0.5em;
  background-color: ${background};
  border: 3px solid ${primary};
  border-radius: 5px;
  :hover {
    cursor: pointer;
    background-color: ${quad};
  }

  @media (max-width: 600px) {
    width: 6em;
    height: 4em;
    margin: 0.3em;
  }
`;

const StyledP = styled.p`
  font-size: 1em;
  margin-top: 0.3em;
  color: ${primaryFont};
  @media (max-width: 600px) {
    font-size: 0.9em;
    margin-top: 0.2em;
  }
`;

const BotomP = styled.p`
  font-size: 1em;
  margin-top: -2.2em;
  color: ${primaryFont};

  @media (max-width: 600px) {
    font-size: 0.9em;
    margin-top: -2.1em;
  }
`;

const Stat = styled.p`
  font-family: "Dosis";
  color: ${secondary};
  font-size: 2em;
  margin-top: -0.55em;

  @media (max-width: 600px) {
    font-size: 1.6em;
    margin-top: -0.7em;
  }
`;

const MiscStats = ({ misc }) => {
  const rollInit = () => {};

  return (
    <React.Fragment>
      <Container>
        <Box>
          <StyledP>Armor</StyledP>
          <Stat>{misc.ac}</Stat>
          <BotomP>Class</BotomP>
        </Box>
        <InitBox>
          <StyledP>Initiative</StyledP>
          <Stat>{`+${misc.initiative}`}</Stat>
        </InitBox>
        <Box>
          <StyledP>Walking</StyledP>
          <Stat>{`${misc.speed} ft.`}</Stat>
          <BotomP>Speed</BotomP>
        </Box>
        <Box>
          <StyledP>Inspired</StyledP>
          <Stat>{misc.inspiration.toString()}</Stat>
        </Box>
        <InitBox>
          <StyledP>Rest</StyledP>
          <Stat>ZZZ</Stat>
        </InitBox>
      </Container>
      <CharacterRollDisplay key="Inititive roll display" />
    </React.Fragment>
  );
};

export default MiscStats;
