import React from "react";
import styled from "styled-components";
import LargeDiceDisplay from "../../Componets/DiceDisplay";
import RollButton from "../../Buttons/DiceButtons/RollButton";
import RollInfoDisplay from "../../Componets/RollInfoDisplay";
import DiceRollResults from "../../Logic/DiceRollResults";

const RollDisplayContainer = styled.div`
  height: 280px;
`;

const DiceNumberContainer = styled.div`
  width: 50px;
  position: relative;
  top: -60%;
  left: calc(50% - 25px);
  text-align: center;
`;

const DiceNumber = styled.p`
  font-family: "Dosis";
  font-size: 2.8em;
  text-align: center;
  z-index: 1;
  user-select: none;
  -webkit-user-select: none;
  cursor: pointer;
`;

const DiceRollButtonContainer = styled.div`
  height: 85px;
  width: 40%;
  margin: 0 auto;
  display: flex;

  @media (max-width: 900px) {
    width: 70%;
  }

  @media (max-width: 600px) {
    width: 96%;
  }
`;

const CenterAlingDiv = styled.div`
  width: 100%;
  text-align: center;
`;

class DiceRoller extends React.Component {
  state = { rolled: "R", type: 20, results: DiceRollResults };

  rollDice = (dice, diceID) => {
    this.setState({ rolled: Math.floor(Math.random() * dice) + 1 });
    this.setState({ type: dice });

    console.log(this.state.results.diceID);
  };

  render() {
    return (
      <div>
        <RollDisplayContainer>
          <LargeDiceDisplay
            dice={this.state.type}
            rolled={this.state.rolled}
            onClick={() => this.rollDice(this.state.type)}
          />
          <DiceNumberContainer>
            <DiceNumber onClick={() => this.rollDice(this.state.type)}>
              {this.state.rolled}
            </DiceNumber>
          </DiceNumberContainer>
        </RollDisplayContainer>
        <CenterAlingDiv>
          <DiceRollButtonContainer>
            <RollButton dice={4} diceID={4} onClick={() => this.rollDice(4)} />
            <RollButton dice={6} diceID={6} onClick={() => this.rollDice(6)} />
            <RollButton dice={8} diceID={8} onClick={() => this.rollDice(8)} />
            <RollButton
              dice={12}
              diceID={12}
              onClick={() => this.rollDice(12)}
            />
            <RollButton
              dice={20}
              diceID={20}
              onClick={() => this.rollDice(20)}
            />
          </DiceRollButtonContainer>
        </CenterAlingDiv>
        <RollInfoDisplay />
      </div>
    );
  }
}

export default DiceRoller;
