import React from "react";
import styled from "styled-components";
import D20 from "../../Images/Dice/D20";
import RollButton from "../../Buttons/DiceButtons/RollButton";

const StyledD20 = styled(D20)`
  margin-top: 55px;
  height: 180px;
`;

const DiceNumber = styled.p`
  font-family: "Dosis";
  font-size: 2.8em;
  text-align: center;
  position: relative;
  left: 50%;
  transform: translate(-50%, -295%);
`;

const RollDisplayContainer = styled.div`
  height: 280px;
`;

const DiceRollButtonContainer = styled.div`
  height: 85px;
  display: flex;
`;

class DiceRoller extends React.Component {
  state = { rolled: "R", type: 20 };

  rollDice = (dice) => {
    this.setState({ rolled: Math.floor(Math.random() * dice) + 1 });
  };

  render() {
    return (
      <div>
        <RollDisplayContainer>
          <StyledD20 />
          <DiceNumber>{this.state.rolled}</DiceNumber>
        </RollDisplayContainer>
        <DiceRollButtonContainer>
          <RollButton dice={12} onClick={() => this.rollDice(12)} />
          <RollButton dice={20} onClick={() => this.rollDice(20)} />
        </DiceRollButtonContainer>
      </div>
    );
  }
}

export default DiceRoller;
