import react from "react";
import styled from "styled-components";
import theme from "../../Logic/theme";

const { primary, secondary, tri, quad, background, primaryColor } = theme;

const Container = styled.div`
  width: calc(100% - 0.35em);
  margin-left: 0.5em;
  height: 6.5em;
  grid-area: save;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  row-gap: 0.25em;
`;

const SaveContainer = styled.div`
  width: 11em;
  height: 2.5em;
  background-color: #fff;
  border: solid 3px ${primary};
  border-radius: 5px;
  display: flex;
  justify-content: space-evenly;

  :hover {
    cursor: pointer;
    background-color: ${secondary};
  }
`;

const IsPro = styled.div`
  width: 0.5em;
  height: 0.5em;
  margin-top: 1em;
  border: 1.5px solid ${quad};
  border-radius: 25px;
  background-color: ${secondary};
`;

const NotPro = styled.div`
  width: 0.5em;
  height: 0.5em;
  margin-top: 1em;
  border: 2px solid ${quad};
  border-radius: 25px;
  background-color: none;
`;

const StyledP = styled.p`
  color: ${background};
  margin-top: 0.5em;
`;

const SaveRolls = ({ saves }) => {
  const renderedSaves = saves.map((saves) => {
    return (
      <SaveContainer key={`${saves.name} save roll container`}>
        {saves.pro ? (
          <IsPro key={`${saves.name} is pro`} />
        ) : (
          <NotPro key={`${saves.name} is not pro`} />
        )}
        <StyledP key={`${saves.name} label`}>{saves.name}</StyledP>
        <StyledP key={`${saves.name} mod`}>{saves.save}</StyledP>
      </SaveContainer>
    );
  });
  return <Container>{renderedSaves}</Container>;
};

export default SaveRolls;
