import React from "react";
import styled from "styled-components";
import Header from "./Header";
import PlayArea from "./MainPlayArea";
import Footer from "./Footer";
import RollButton from "../Buttons/RollMenuItem";
import HistoryButton from "../Buttons/HistoryMenuItem";
import CharacterButton from "../Buttons/CharacterMenuItem";
//import Spinner from "../Componets/Spinner";

const PlayContainer = styled.div`
  text-align: center;
`;

const MainContainer = ({ character }) => {
  return (
    <PlayContainer>
      <Header name={character.name} />
      <PlayArea character={character} />
      <Footer />
      <RollButton />
      <HistoryButton />
      <CharacterButton active={true} />
    </PlayContainer>
  );
};
export default MainContainer;
