import React from "react";
import styled from "styled-components";
import D20 from "../../Images/Dice/D20";
import LargeDiceDisplay from "../../Componets/DiceDisplay";
import RollButton from "../../Buttons/DiceButtons/RollButton";

const StyledDisplayDice = styled(LargeDiceDisplay)`
  margin-top: 55px;
  height: 180px;
`;

const DiceNumber = styled.p`
  font-family: "Dosis";
  font-size: 2.8em;
  text-align: center;
  position: relative;
  left: 50%;
  transform: translate(-50%, -295%);
`;

const RollDisplayContainer = styled.div`
  height: 280px;
`;

const DiceRollButtonContainer = styled.div`
  height: 85px;
  width: 45%;
  margin: 0 auto;
  display: flex;

  @media (max-width: 600px) {
    width: 96%;
  }
`;

const CenterAlingDiv = styled.div`
  width: 100%;
  text-align: center;
`;

class DiceRoller extends React.Component {
  state = { rolled: "R", type: 20 };

  rollDice = (dice) => {
    this.setState({ rolled: Math.floor(Math.random() * dice) + 1 });
  };

  render() {
    return (
      <div>
        <RollDisplayContainer>
          <StyledDisplayDice dice={this.type} />
          <DiceNumber>{this.state.rolled}</DiceNumber>
        </RollDisplayContainer>
        <CenterAlingDiv>
          <DiceRollButtonContainer>
            <RollButton dice={4} onClick={() => this.rollDice(4)} />
            <RollButton dice={6} onClick={() => this.rollDice(6)} />
            <RollButton dice={8} onClick={() => this.rollDice(8)} />
            <RollButton dice={12} onClick={() => this.rollDice(12)} />
            <RollButton dice={20} onClick={() => this.rollDice(20)} />
          </DiceRollButtonContainer>
        </CenterAlingDiv>
      </div>
    );
  }
}

export default DiceRoller;
