import React, { useEffect, useState } from "react";
import styled from "styled-components";
import theme from "../../Logic/theme";
import CharacterRollDisplay from "./ChracterRollDIsplay";

const { primary, secondary, tri, quad, background, primaryFont } = theme;

const Container = styled.div`
  width: 100%;
  font-family: "Dosis";
  font-size: 1.2em;
  font-weight: bold;
  border: 3px solid ${primary};
  border-radius: 5px;
  background-color: #fff;
`;

const SkillContainer = styled.div`
  margin: 0.75em 0;
  width: 100%;
  display: flex;
`;

const ProBox = styled.div`
  width: 0.75em;
  margin-left: 1em;
  margin-right: 0.1em;
`;

const ModBox = styled.div`
  width: 3.5em;
`;

const SkillBox = styled.div`
  height: 25px;
  width: calc(100% - 5em);
  margin: 0 0.15em 0.25em;
  text-align: center;
`;

const BonusBox = styled.div`
  width: 1.5em;
  height: 1.5em;
  margin-top: -0.25em;
  margin-right: 1em;
  padding-top: 0.25em;
  text-align: center;
  border: 2px solid ${primary};
  border-radius: 5px;
  float: right;
`;

const IsPro = styled.div`
  width: 8px;
  height: 8px;
  margin: 0.4em 10px 0;
  border: 1.5px solid ${quad};
  border-radius: 25px;
  background-color: ${secondary};
`;

const NotPro = styled.div`
  width: 8px;
  height: 8px;
  margin: 0.4em 10px 0;
  border: 2px solid ${quad};
  border-radius: 25px;
  background-color: none;
`;

const Hr = styled.hr`
  width: 98;
  border: 0.5px solid ${primary};
`;

const Skills = ({ skills }) => {
  const [lastRoll, setLastRoll] = useState("");

  const skillCheck = (name, mod) => {
    const roll = Math.floor(Math.random() * 20 + 1);
  };

  const RenderedSkills = skills.map((skills) => {
    return (
      <React.Fragment>
        <Hr />
        <SkillContainer>
          <ProBox>{skills.pro ? <IsPro /> : <NotPro />}</ProBox>
          <ModBox>{skills.mod}</ModBox>
          <SkillBox>{skills.name}</SkillBox>
          <BonusBox>{skills.bonus}</BonusBox>
        </SkillContainer>
      </React.Fragment>
    );
  });

  return (
    <Container>
      <SkillContainer>
        <ProBox>Pro</ProBox>
        <ModBox>Mod</ModBox>
        <SkillBox>Skill</SkillBox>
        <BonusBox>Bonus</BonusBox>
      </SkillContainer>
      {RenderedSkills}
    </Container>
  );
};

export default Skills;
